import React from "react";
import marked from "react-marked";
marked.setOptions({
  breaks:true
  });



function Preview(props){


    return(
        <div
      dangerouslySetInnerHTML={{
        __html: marked(props.markdown)
      }}
      id='preview'
    />
    )
}
export default Preview;
